mapper.update:
update music set mname = #{mname,jdbcType=VARCHAR}, sid = #{sid,jdbcType=INTEGER},
type = #{type,jdbcType=VARCHAR}, myear = #{myear,jdbcType=DATE},
location = #{location,jdbcType=VARCHAR}, aid = #{aid,jdbcType=INTEGER}, mvid = #{mvid,jdbcType=INTEGER}, duration = #{duration,jdbcType=VARCHAR}
where mid = #{mid,jdbcType=INTEGER}
